import React, { useState, useEffect, useCallback, useRef } from 'react';
import dh from '../assets/helpers/domhelper.js';

function Sort() {
  const [open, setOpen] = useState(false);
  const [sortTitle, setSortTitle] = useState(0);
  const sortList = ['популярности', 'цене', 'алфавиту'];

  useEffect(() => {
    if (open) dh.addEventListener('click', close);
    return () => dh.removeEventListener('click', close);
  }, [open]);

  const close = useCallback((e) => {
    console.log(e.target);
    // if (e.target.parentElement.parentElement.className === 'sort__popup') {
    //   setOpen(false);
    // }
    if (e.target.className !== 'sort__title') setOpen(false);
  }, []);

  const setSortTitleHandler = (event, idx) => {
    event.stopPropagation();
    setSortTitle(idx);
  };

  return (
    <div className="sort">
      <div className="sort__label">
        <span className="icon-arrow-top" />
        <b>Сортировка по:</b>
        <div onClick={() => setOpen((prev) => !prev)}>
          <span className="sort__title">{sortList[sortTitle]}</span>
        </div>
      </div>
      {open && (
        <div className="sort__popup">
          <ul>
            {sortList.map((el, idx) => (
              <li
                key={idx}
                onClick={(event) => setSortTitleHandler(event, idx)}
                className={sortTitle === idx ? 'active' : ''}>
                {sortList[idx]}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Sort;
