import React, { useState } from 'react';

function Categories() {
  const [activeIndex, setActiveIndex] = useState(0);

  const selectActiveIndex = (idx) => {
    setActiveIndex(idx);
  };

  const categories = ['', '', '', ];

  return (
    <div className="categories">
      <ul>
        <li>
          <button
            onClick={() => selectActiveIndex(0)}
            className={activeIndex === 0 ? 'active' : ''}>
            Все
          </button>
        </li>
        <li>
          <button
            onClick={() => selectActiveIndex(1)}
            className={activeIndex === 1 ? 'active' : ''}>
            Мясные
          </button>
        </li>
        <li>
          <button
            onClick={() => selectActiveIndex(2)}
            className={activeIndex === 2 ? 'active' : ''}>
            Вегетарианская
          </button>
        </li>
        <li>
          <button
            onClick={() => selectActiveIndex(3)}
            className={activeIndex === 3 ? 'active' : ''}>
            Гриль
          </button>
        </li>
        <li>
          <button
            onClick={() => selectActiveIndex(4)}
            className={activeIndex === 4 ? 'active' : ''}>
            Острые
          </button>
        </li>
        <li>
          <button
            onClick={() => selectActiveIndex(5)}
            className={activeIndex === 5 ? 'active' : ''}>
            Закрытые
          </button>
        </li>
      </ul>
    </div>
  );
}

export default Categories;
