import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  categoryIndex: 0,
  sortTitle: {
    name: 'популярности', 
    sort: 'rating'
  }
};

const filterSlice = createSlice({
  name: 'filters',
  initialState,
  reducers: {
    setCategoryIndex(state, actions) {
      state.categoryIndex
    }
  }
});
